mapper.update:
update notice
set notice_readcount = notice_readcount + 1
where notice_no = #{notice_no}

update Notice set notice_title = #{ notice_title }, notice_content = #{ notice_content },
notice_file = #{ notice_file }, notice_refile = #{ notice_refile }
notice_file = null, notice_refile = null
where notice_no = #{ notice_no }
