mapper.select:

select *
from (select rownum rnum, bn_no, bn_title, bn_date, bn_file, bn_refile, bn_state
	from(select * from mainbn order by bn_no desc))
where rnum >= #{startRow} and rnum <= #{endRow}

mapper.update:
update mainbn set bn_title = #{ bn_title }, bn_state = #{ bn_state },
bn_file = #{ bn_file }, bn_refile = #{ bn_refile }
bn_file = null, bn_refile = null
where bn_no = #{ bn_no }
